"use strict";!function(){var app=angular.module("app");app.controller("layout.standard.blogListController",["$scope","$state","$mdDialog","Notification","$stateParams","BlogService",function($scope,$state,$mdDialog,Notification,$stateParams,BlogService){function loadBlogList(data){BlogService.get(data).then(function(data){$scope.count=data.data.count,$scope.showLoader=!1,$scope.blogList=data.data.results})}loadBlogList({page_size:10,page:1}),$scope.currentPage=1,$scope.selectedBlog=[],$scope.maxSize=10,$scope.showLoader=!0,$scope.setPage=function(pageNo){$scope.showLoader=!0,$scope.currentPage=pageNo,loadBlogList({page_size:10,page:pageNo})},$scope.createBlog=function(data){$state.go("adminDashboard.blog.add")},$scope.editBlog=function(data){return 0===data.length?void Notification.error({message:"Please select atleast one item to edit",positionX:"right",positionY:"top"}):data.length>1?void Notification.error({message:"Please select one item to edit",positionX:"right",positionY:"top"}):void $state.go("adminDashboard.blog.edit",{blogId:data[0].id})},$scope.viewBlog=function(data){return 0===data.length?void Notification.error({message:"Please select atleast one item to edit",positionX:"right",positionY:"top"}):data.length>1?void Notification.error({message:"Please select one item to edit",positionX:"right",positionY:"top"}):void $state.go("adminDashboard.blog.view",{blogId:data.id})},$scope.selectedValue=function(data,index,value){value?$scope.selectedBlog.push(data):$scope.selectedBlog.map(function(item,index){item.id===data.id&&($scope.selectedBlog.splice(index,1),$scope.selectedBlog.length-1)})}}])}();