"use strict";!function(){var app=angular.module("app");app.controller("layout.standard.ViewblogController",["$scope","BlogService","$state","$mdDialog","Notification","$stateParams",function($scope,BlogService,$state,$mdDialog,Notification,$stateParams){BlogService.getOne($stateParams.blogId).then(function(res){$scope.blog=res.data}),$scope.cancel=function(){$state.go("layout.standard.blog.page")},$scope.save=function(data){data.is_posted=!0,BlogService.update(data.id,data).then(function(res){Notification.success({message:"successfully published",positionX:"right",positionY:"top"}),$scope.blog=res.data,$scope.cancel()})}}])}();